mapper.select:
/** insertFile ::: 파일을 DB에 저장한다. seq 자동증가키*/
 
INSERT INTO HYGERA.DBO.FILE_MST_T ( 
	FILE_NM
	,FILE_TITLE
	,FILE_PATH 
	,FILE_SIZE 
	,CREATED_AT
	,CREATED_ID
) VALUES (
	#{fileNm} 
	,#{fileTitle}
	,#{filePath}
	,#{fileSize}
	,getdate()
	,'' 
)

/** selectFileSeq ::: file_id 가져오기.*/

SELECT 
	 A.SEQ AS seq
	FROM HYGERA.DBO.FILE_MST_T A
WHERE 1 = 1


	AND A.FILE_TITLE = #{fileTitle}

/** selectFilePath ::: file 주소 가져오기.*/
	
SELECT 
	A.FILE_PATH AS filePath
	FROM HYGERA.DBO.FILE_MST_T A
WHERE 1 = 1


	AND A.SEQ = #{fileId}

mapper.delete:
/** fileDelete ::: 파일을 단건 삭제한다.*/

	DELETE FROM HYGERA.DBO.FILE_MST_T
	WHERE SEQ = #{seq}
